package br.image.datauri;

import java.awt.Image;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.util.logging.Level;
import java.util.logging.Logger;
import javax.imageio.ImageIO;
import javax.swing.ImageIcon;
import javax.xml.bind.DatatypeConverter;
import sun.misc.BASE64Decoder;

/**
 *
 * @author Saulobt@gmail.com
 */
public class ImageDadaURI {

    public static String JPG = "jpg";
    public static String JPEG = "jpeg";
    public static String PNG = "png";
    public static String GIF = "gif";

    public ImageDadaURI() {
    }

    public static String convertImageToDataURI(File imageFile, String imageType) throws IOException {

        StringBuffer resultImage = new StringBuffer();

        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        if (imageType == null) {
            imageType = ImageDadaURI.PNG;
        }
        ImageIO.write(readImage(imageFile), imageType, baos);
        resultImage.append("data:image/" + imageType + ";base64,");
        resultImage.append(DatatypeConverter.printBase64Binary(baos.toByteArray()));

        return resultImage.toString();
    }
    
    public static String convertImageToBase64(File imageFile, String imageType) throws IOException {

        StringBuffer resultImage = new StringBuffer();

        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        if (imageType == null) {
            imageType = ImageDadaURI.PNG;
        }
        ImageIO.write(readImage(imageFile), imageType, baos);
        //resultImage.append("data:image/" + imageType + ";base64,");
        resultImage.append(DatatypeConverter.printBase64Binary(baos.toByteArray()));

        return resultImage.toString();
    }

    public static byte[] convertDataURIToImageByte(String base64String) throws IOException {
        //String base64String = "";
        BASE64Decoder decoder = new BASE64Decoder();
        byte[] decodedBytes = decoder.decodeBuffer(base64String);
        //System.out.println("Decoded upload data : " + decodedBytes.length);
        BufferedImage image = ImageIO.read(new ByteArrayInputStream(decodedBytes));
        return decodedBytes;
    }
    
    public static ImageIcon convertDataURIToImageIcon(String base64String) throws IOException {
        //String base64String = "";
        BASE64Decoder decoder = new BASE64Decoder();
        byte[] decodedBytes = decoder.decodeBuffer(base64String);
        //System.out.println("Decoded upload data : " + decodedBytes.length);
        BufferedImage image = ImageIO.read(new ByteArrayInputStream(decodedBytes));
        return new ImageIcon(decodedBytes);
    }
    
    public static Image convertDataURIToImage(String base64String) throws IOException {
        //String base64String = "";
        BASE64Decoder decoder = new BASE64Decoder();
        byte[] decodedBytes = decoder.decodeBuffer(base64String);
        //System.out.println("Decoded upload data : " + decodedBytes.length);
        BufferedImage image = ImageIO.read(new ByteArrayInputStream(decodedBytes));
        return new ImageIcon(decodedBytes).getImage();
    }

    public static String convertImageToDataURI(File imageFile, String imageType, File saveResultPathFile) throws IOException {
        String result = convertImageToDataURI(imageFile, imageType);
        saveResultConvertImageDataURI(saveResultPathFile, result);
        return result;
    }

    public static BufferedImage readImage(File imageFile) throws IOException {
        BufferedImage imageBuffer = null;
        imageBuffer = ImageIO.read(imageFile);
        return imageBuffer;
    }

    public static void saveResultConvertImageDataURI(File imageFile, String content) throws IOException {
        FileWriter fw = new FileWriter(imageFile);
        fw.write(content);
        fw.flush();
        fw.close();
    }

    public static void main(String[] args) {
        try {
            //ImageDadaURI.convertImageToDataURI(new File("Duke_(Java_mascot)_waving.svg.png"), ImageDadaURI.PNG)
            System.out.println(ImageDadaURI.convertImageToDataURI(new File("CocaCola350ml.png"), ImageDadaURI.PNG, new File("resultConvert.txt")));
            //ImageDadaURI.convertDataURIToImage("");
        } catch (IOException ex) {
            Logger.getLogger(ImageDadaURI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }
}
